const Discord = require('discord.js');
const fs = require('fs');
var item = tempVars("itemGiving");
var time = tempVars("time");
var winners = tempVars("winners");

var giveaway = {
    "itemGivingAway": "",
    "time": 0,
    "winner": 1,
    "finishUnix": 0,
    "channel": "",
    "server": msg.guild.id,
    "giveawayMessageID": "",
    "id": Math.floor(Math.random() * 999999) + 100000
}


if (item != null && time != null) {
    giveaway["itemGivingAway"] = item;
    giveaway["channel"] = msg.channel.id;

    if (!isNaN(winners)) {
        if (parseInt(winners) > 0) {
            giveaway["winner"] = parseInt(winners);
        }
    }
    var tempTime = time;
    if (tempTime.includes("d") || tempTime.includes("h") || tempTime.includes("w") || tempTime.includes("m")) {
        let timeUp;

        if (tempTime.includes("d")) {
            timeUp = "d";
        } else if (tempTime.includes("h")) {
            timeUp = "h";
        } else if (tempTime.includes("w")) {
            timeUp = "w";
        } else if (tempTime.includes("m")) {
            timeUp = "m";
        }

        tempTime = tempTime.split(timeUp);
        if (!isNaN(tempTime[0])) {
            tempTime[0] = parseInt(tempTime[0]);
            switch (timeUp) {
                case "d":
                    giveaway["time"] = tempTime[0] * 86400;
                    break;
                case "h":
                    giveaway["time"] = tempTime[0] * 3600;
                    break;
                case "w":
                    giveaway["time"] = tempTime[0] * 604800;
                    break;
                case "m":
                    giveaway["time"] = tempTime[0] * 60;
                    break;
            }
            giveaway["finishUnix"] = Math.floor(Date.now() / 1000) + giveaway["time"];
            var giveawayEmbed = new Discord.RichEmbed({
                color: 0x2E7F98,
                author: {
                    name: '🎉 New Giveaway 🎉',
                    icon_url: (client || this.getDBM().Bot.bot).user.avatarURL
                },
                description: `New giveaway for **${giveaway["itemGivingAway"]}**\n\nReact with 🎉 to enter the giveaway!`,
                footer: {
                    text: `The giveaway ends on ${new Date(giveaway["finishUnix"] * 1000).toLocaleString("en-US", { timeZone: 'UTC' })} UTC | ${giveaway["winner"]} Winner(s) | ID: ${giveaway["id"]}`
                }
            })

            msg.channel.send(giveawayEmbed)
                .then(giveMsg => {
                    giveMsg.react("🎉");
                    giveaway["giveawayMessageID"] = giveMsg.id;
                    fs.readFile('./data/giveawayData.json', function read(err, data) {
                        if (err) {
                            throw err;
                        }
                        data = JSON.parse(data);

                        if (!data.hasOwnProperty(msg.guild.id)) {
                            data[msg.guild.id] = [];
                        }

                        data[msg.guild.id].push(giveaway);

                        fs.writeFile("./data/giveawayData.json", JSON.stringify(data), function(err) {
                            if (err) {
                                return console.log(err);
                            }
                        });
                    });
                })

        }

    } else {
        msg.channel.send("❌ You did not enter a valid date format!\n\n1w | 1d | 1h | 1m");
    }

} else {
    msg.channel.send("❌ Please enter atleast an item you are gonig to giveaway and a time!");
}
