const fs = require('fs');
const Discord = require('discord.js');
let giveawaysDue = [];
var timeNow = Math.floor(Date.now() / 1000);
var counter = 0;
var interval;
if (globalVars("giveawayRan") == null) {
    this.storeValue(1,3,"giveawayRan",cache);

    fs.readFile('./data/giveawayData.json', async function read(err, data) {
        if (err) {
            throw err;
        }
        data = JSON.parse(data);

        for (var key in data) {
            if (data.hasOwnProperty(key)) {
                let giveaways = data[key];
                let dueGive = await giveaways.filter(({
                    finishUnix
                }) => finishUnix <= timeNow);
                if (dueGive.length > 0) {
                    dueGive.forEach(async f => {
                        giveawaysDue.push(f);
                        giveaways.splice(giveaways.findIndex(e => e.id === f.id), 1)
                    });

                    fs.writeFile("./data/giveawayData.json", JSON.stringify(data), function(err) {
                        if (err) {
                            return console.log(err);
                        }
                    });
                }
            }
        }
        if (giveawaysDue.length > 0) {
            interval = setInterval(drawWinners, 2000);
        }

    })
}

async function drawWinners() {
        var channelToFetch = await client.channels.get(giveawaysDue[counter]["channel"]);
        if (channelToFetch != null) {
            var giveawayMessage = await channelToFetch.fetchMessage(giveawaysDue[counter]["giveawayMessageID"]);
            var giveawayUsers = await giveawayMessage.reactions.get('🎉').fetchUsers();
            giveawayUsers = giveawayUsers.array();
            giveawayUsers = await giveawayUsers.filter(object => object.id != client.user.id);
            var numberOfWinners = parseInt(giveawaysDue[counter]["winner"]);
            var gw = giveawayMessage.embeds[0];

            var giveawayEmbed = new Discord.RichEmbed({
                color: 0x000000,
                author: {
                    name: '🎉 Giveaway Ended 🎉',
                    icon_url: (client || this.getDBM().Bot.bot).user.avatarURL
                },
                description: gw.description,
                footer: {
                    text: gw.footer.text
                }
            })
            var winnerArray = [];
            var winnerText = "";
            if (giveawayUsers.length > 0) {
                if (giveawayUsers.length > numberOfWinners) {
                    for (var i = 0; i < numberOfWinners; i++) {
                        var randomNumber = Math.floor(Math.random() * giveawayUsers.length);
                        var randomuser = giveawayUsers[randomNumber];
                        winnerArray.push(randomuser);
                        giveawayUsers.splice(giveawayUsers.indexOf(randomNumber), randomNumber);
                    }
                } else if (giveawayUsers.length <= numberOfWinners) {
                    for (i = 0; i < giveawayUsers.length; i++) {
                        winnerArray.push(giveawayUsers[i]);
                    }
                }

                for (i = 0; i < winnerArray.length; i++) {
                    winnerText = winnerText + winnerArray[i] + '\n';
                }

                giveawayEmbed.description = giveawayEmbed.description + `\n\nThe winners are..\n${winnerText}`;
                giveawayMessage.edit(giveawayEmbed)
                channelToFetch.send(`🎉 Congratulations! 🎉 \n ${winnerText} \nThey have won **${giveawaysDue[counter]["itemGivingAway"]}**`)

            } else {
                giveawayEmbed.description = giveawayEmbed.description + "\n\nNo one entered the giveaway and thus no one won."
                giveawayMessage.edit(giveawayEmbed);
                channelToFetch.send(`🎉 Giveaway Ended 🎉\n\nNo one entered the giveaway and thus no one won.`)
            }
        }
        counter += 1;
        if (counter >= giveawaysDue.length) {
            clearInterval(interval);
        }
    }

